insert  into `page`(`id`,`title`,`intro`,`content`,`section`,`created_at`,`modified_at`,`is_published`,`publicated_at`,`can_be_deleted`,`is_deleted`) values (1,'L\'histoire du club','Victus universis caro ferina est lactisque abundans copia qua sustentantur, et herbae multiplices et siquae alites capi per aucupium possint, et plerosque mos vidimus frumenti usum et vini penitus ignorantes.','<p>\r\n</p><p><b>Montius nos tumore inusitato quodam et novo ut rebellis et maiestati \r\nrecalcitrantes Augustae per haec quae strepit incusat iratus nimirum \r\nquod contumacem praefectum, quid rerum ordo postulat ignorare \r\ndissimulantem formidine tenus iusserim custodiri.</b></p>\r\n<p>Ergo ego senator inimicus, si ita vultis, homini, amicus esse, sicut \r\nsemper fui, rei publicae debeo. Quid? si ipsas inimicitias, depono rei \r\npublicae causa, quis me tandem iure reprehendet, praesertim cum ego \r\nomnium meorum consiliorum atque factorum exempla semper ex summorum \r\nhominum consiliis atque factis mihi censuerim petenda.</p>\r\n<p>Itaque verae amicitiae difficillime reperiuntur in iis qui in \r\nhonoribus reque publica versantur; ubi enim istum invenias qui honorem \r\namici anteponat suo? Quid? Haec ut omittam, quam graves, quam difficiles\r\n plerisque videntur calamitatum societates! Ad quas non est facile \r\ninventu qui descendant. Quamquam Ennius recte.</p>\r\n<p></p>',1,'2014-01-16 12:22:14','2014-01-17 12:47:24',1,'2014-01-17 12:47:24',0,0),(2,'Les infos pratiques',NULL,'<h3>Siège social</h3><b>Contact:</b>&nbsp;<div>Siège: 01.43.09.79.21<br>Fatima Bienvenu:&nbsp;06.46.26.65.46<div><br><b>Adresse:</b><br>Le club des paquerêttes<br>100 Rue du 11 Novembre<br>93330 Neuilly Sur Marne<br><br><br>\r\n</div></div>',1,'2014-01-16 00:07:42','2014-01-16 00:07:49',1,'2014-01-16 00:08:00',0,0),(5,'championnat',NULL,'gf',12,'2014-01-16 12:23:03','2014-01-16 13:15:49',0,NULL,1,0),(6,'championnat',NULL,',,',11,'2014-01-16 12:23:10','2014-01-16 13:11:11',0,NULL,1,1),(7,'Les cours',NULL,'<p></p><h3>Danse Fitness Zumba</h3><img width=\"240\" height=\"160\" src=\"http://www.mairiedeneuillysurmarne.com/wordpress/wp-content/uploads/2012/01/modern-jazz-470310-240x160.jpg\" class=\"img_article_unique wp-post-image\" alt=\"modern-jazz-470310\" title=\"modern-jazz-470310\" style=\"cursor: nw-resize;\"><p><strong>Gymnase Jules Ferry</strong></p><p>Le club propose des cours des cours de danse pour les enfants et adultes avec&nbsp;participation à des championnats.</p><p>Des cours de fitness (body combat, steps, abdos, streching) et zumba à partir de 15 ans.</p><p>Début des cours la semaine du 9 septembre</p><p>2 cours d’essai pour les nouveaux et inscriptions fin septembre</p><p>Accueil des enfants à partir de&nbsp;4 ans, pour les adultes cours de danse et de fitness.</p><p>Cotisation annuelle de 100 € à 160 € en fonction des tranches d’âge. Fournir certificat médical</p><br><p></p>',11,'2014-02-01 17:12:46','2014-02-01 17:12:49',1,'2014-02-01 17:12:49',1,0);

